import React, { useState } from 'react';
import chartStyle from './chart-home.module.scss';
import { Line } from 'react-chartjs-2';

const ChartHome = () => {

    const options = {
        responsive: true, maintainAspectRatio: false,
        scales: {
            yAxes: [{
                ticks: {
                    fontColor: "white",
                    stepSize: 10,
                    beginAtZero: true
                }
            }],
            xAxes: [{
                gridLines: {
                    color: "rgba(0, 0, 0, 0)",
                },
                ticks: {
                    fontColor: "white",
                    //fontSize: 14,
                    //stepSize: 1,
                    //beginAtZero: true
                }
            }]
        }, 
        legend: {
            display: false
        },
        tooltips: {
            callbacks: {
               label: function(tooltipItem: any) {
                      return tooltipItem.yLabel;
               }
            }
        }, 

    };

    const data = (canvas: any) => {
        const ctx = canvas.getContext("2d")
        var gradient = ctx.createLinearGradient(0, 0, 0, 100);
        gradient.addColorStop(0, "#7467ef");
        gradient.addColorStop(1, "rgba(0,0,0,0.1)");
        return {
            labels: ['ינואר', 'פבואר', 'מרץ', 'אפריל', 'מאי', 'יוני', 'יולי', 'אוגוסט', 'ספטמבר', 'אוקטובר', 'נובמבר', 'דצמבר'],
            datasets: [
                {
                    label: 'לקוחות',
                    data: [50, 0, 50, 20, 0, 20, 50, 20, 50, 0, 50, 20],
                    backgroundColor: gradient,
                    borderColor: "#fafafa",
                    borderWidth: 1.5,


                }
            ]
        }
    }

    return (
        <div className={chartStyle.Chart}>
            <Line
                options={options}
                data={data}
                height={300}

            />
        </div>
    )
}

export default ChartHome;
