import React, { useState } from 'react';
import chartStyle from './chart-home.module.scss';
import { Line } from 'react-chartjs-2';

const ChartHome = () => {

    const options = {
        responsive: true, maintainAspectRatio: false,
        scales: {
            xAxes: [{
                gridLines: {
                    color: "rgba(0, 0, 0, 0)",
                }
            }]
        }, 
        legend: {
            display: false
        },
        tooltips: {
            callbacks: {
               label: function(tooltipItem: any) {
                      return tooltipItem.yLabel;
               }
            }
        }, 

    };

    const data = (canvas: any) => {
        const ctx = canvas.getContext("2d")
        const gradient = ctx.createLinearGradient(100, 0, 100, 0);

        return {
            labels: ['ינואר', 'פבואר', 'מרץ', 'אפריל', 'מאי', 'יוני', 'יולי', 'אוגוסט', 'ספטמבר', 'אוקטובר', 'נובמבר', 'דצמבר'],
            datasets: [
                {
                    label: 'לקוחות',
                    data: [50, 20, 50, 20, 50, 20, 50, 20, 50, 20, 50, 20],
                    backgroundColor: gradient, // Put the gradient here as a fill color
                    borderColor: "#FFFAF0",
                    borderWidth: 2,
                    pointColor: "#fff",
                    pointStrokeColor: "#ff6c23",
                    pointHighlightFill: "red",
                    pointHighlightStroke: "#ff6c23",
                }
            ]
        }
    }

    return (
        <div className={chartStyle.Chart}>
            <Line
                options={options}
                data={data}
                height={300}
                width={300}

            />
        </div>
    )
}

export default ChartHome;
