import React, { Component } from 'react'
import './calendar.css'
import { Calendar } from "@fullcalendar/core";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import interactionPlugin from "@fullcalendar/interaction";
import rrulePlugin from "@fullcalendar/rrule";



class CalendarUser extends Component {
    [x: string]: any;
    constructor(props: any) {
        super(props);
        this.state = {
            calendar: null
        };
        this.calendarRef = React.createRef();
    }

    componentDidMount() {
        const calendar = new Calendar(this.calendarRef.current, {
            plugins: [
                dayGridPlugin,
                timeGridPlugin,
                listPlugin,
                rrulePlugin,
                interactionPlugin
            ],
            defaultView: "timeGridWeek",
            header: {
                center: "title",
                right: "today prev,next",
                left: "dayGridMonth, timeGridWeek"
            },
            timezone: "UTC",
            selectable: true,
            editable: true,

            events: [
                {
                    title: "RRule event",
                    rrule:
                        "DTSTART:20190610T103000\nRRULE:FREQ=DAILY;INTERVAL=1;UNTIL=20190801\nEXDATE:20190618T103000Z\nEXDATE:20190619T103000",

                    duration: "02:00"
                }
            ],

            eventDrop: this.eventDrop
        });

        calendar.render();
        this.setState({ calendar });
    }

    eventDrop = info => {
        console.log("DROP", info.event.start);
    };

    render() {
        return <div ref={this.calendarRef} />;
    }
}


export default CalendarUser;
