import React, { useState } from 'react';
import menuUserStyle from './menu-user.module.scss';
import Dropdown from './components/dropdown/dropdown.menu';
import { NavLink } from 'react-router-dom';
import { FaRegCalendarAlt } from 'react-icons/fa';
import { BsGraphUp } from 'react-icons/bs';
import { MdSettings, MdKeyboardArrowLeft, MdPeople, MdDashboard } from 'react-icons/md';


const MenuUser = () => {
    const [ShowDropdown, setShowDropdown] = useState<boolean>(false);
    const [showMenu, setShowMenue] = useState<boolean>(false);

    const menuButtonForMobile = () => {
        return (
            <div className={menuUserStyle.ButtonForMobile}>
                <input
                    type="checkbox"
                    className={menuUserStyle.Checkbox}
                    id="navi-toggle"
                    onClick={() => setShowMenue(!showMenu)}
                />
                <label htmlFor="navi-toggle" className={menuUserStyle.Navigation_button} >
                    <span className={menuUserStyle.Icon}>&nbsp;</span>
                </label>
            </div>
        )
    }
    if (!showMenu) return <div>{menuButtonForMobile()}</div>;

    return (
        <div className={menuUserStyle.Menu}>
            {menuButtonForMobile()}
            <p className={menuUserStyle.Logo}>
                Queue
            </p>
            <div className={menuUserStyle.User}>
                <p>שלום, דור לוי.</p>
            </div>
            <hr />
            <div className={menuUserStyle.Items}>
                <NavLink className={menuUserStyle.MenuItem} activeClassName={menuUserStyle.Current} exact to='/'>
                    <div className={menuUserStyle.Text}>
                        <MdDashboard />
                        <span>בית</span>
                    </div>
                </NavLink>

                <NavLink className={menuUserStyle.MenuItem} activeClassName={menuUserStyle.Current} to='/queue'>
                    <div className={menuUserStyle.Text}>
                        <FaRegCalendarAlt />
                        <span> יומן תורים</span>
                    </div>
                </NavLink>

                <NavLink className={menuUserStyle.MenuItem + " " + menuUserStyle.Dropdown} activeClassName={menuUserStyle.Current} to='/customers'>
                    <div className={menuUserStyle.Text} onClick={() => { setShowDropdown(!ShowDropdown) }}>
                        <MdPeople />
                        <span >לקוחות</span>
                        <MdKeyboardArrowLeft size="1.3rem" className={menuUserStyle.ArrowIcon}
                            style={ShowDropdown ? { transform: ' rotate(-90deg)' } : {}} />
                    </div>

                    <Dropdown items={[{ title: 'כל הלקוחות', url: '/allcustomers' }, { title: 'לקוחות מהשבוע', url: '/customerweek' }]}
                        show={ShowDropdown} />
                </NavLink>

                <NavLink className={menuUserStyle.MenuItem} activeClassName={menuUserStyle.Current} to='/s'>
                    <div className={menuUserStyle.Text}>
                        <BsGraphUp />
                        <span>סטטיסטיקות</span>
                    </div>
                </NavLink>

                <NavLink className={menuUserStyle.MenuItem} activeClassName={menuUserStyle.Current} to='/settings'>
                    <div className={menuUserStyle.Text}>
                        <MdSettings />
                        <span>הגדרות</span>
                    </div>
                </NavLink>
            </div>
        </div>
    )
}

export default MenuUser;
