import moment from 'moment';
import { Events } from '../../../../../models/system/events';
import { Event } from '../../../../../models/system/event';

export const dateformat = "yyyy/MM/DD";
export const eventFormat = "YYYY-MM-DD HH:mm";
export const hebDays = ["א'", "ב'", "ג'", "ד'", "ה'", "ו'", "ש'"];


// Get week number 
// Return all the dates of week
export const getWeekDaysByWeekNumber = (weeknumber?: number) => {
    var date = moment().isoWeek(weeknumber || 1).startOf("week"), weeklength = 6, result = [];
    while (weeklength--) {
        result.push(date.format(dateformat));
        date.add(1, "day")
    }
    return result;
}

export const monthHebToEng = (month: string) => {
    switch (month) {
        case 'ינואר':
            return 'January';
        case 'פבואר':
            return 'February';
        case 'מרץ':
            return 'March';
        case 'אפריל':
            return 'May';
        case 'מאי':
            return 'May';
        case 'יוני':
            return 'June';
        case 'יולי':
            return 'July';
        case 'אוגוסט':
            return 'August';
        case 'ספטמבר':
            return 'September';
        case 'אוקטובר':
            return 'October';
        case 'נובמבר':
            return 'November';
        case 'דצמבר':
            return 'December';
    }
}

export const monthNumberToHeb = (month: number): string => {
    switch (month) {
        case 1:
            return 'ינואר';
        case 2:
            return 'פבואר';
        case 3:
            return 'מרץ';
        case 4:
            return 'אפריל';
        case 5:
            return 'מאי';
        case 6:
            return 'יוני';
        case 7:
            return 'יולי';
        case 8:
            return 'אוגוסט';
        case 9:
            return 'ספטמבר';
        case 10:
            return 'אוקטובר';
        case 11:
            return 'נובמבר';
        default:
            return 'דצמבר'
    }
}


// Dictionary of events - id: Week Number , value: Array of events such that place i in the array represent a day 
// (0 - Sunday... 2 - thursday)
export const events: { [id: number]: Events } = {}
events[20] = { events: new Array<Event[]>(7) }
events[21] = { events: new Array<Event[]>(7) }
events[22] = { events: new Array<Event[]>(7) }
events[23] = { events: new Array<Event[]>(7) }

const sunday: Event[] = []; // All the events in sunday
const monday: Event[] = [];
const wensday: Event[] = [];

sunday.push({
    id: 1,
    title: 'תור ראשון',
    employeeId: 1,
    userId: "0502243024",
    start: moment("2020-05-10 14:45").format(eventFormat),
    end: moment("2020-05-10 15:30").format(eventFormat)
});

monday.push({
    id: 2,
    title: 'תור שני',
    employeeId: 1,
    userId: "0502243024",
    start: moment("2020-05-11 07:45").format(eventFormat),
    end: moment("2020-05-11 08:00").format(eventFormat)
});

wensday.push({
    id: 3,
    title: 'תור שלישי',
    employeeId: 1,
    userId: "0502243024",
    start: moment("2020-05-13 11:45").format(eventFormat),
    end: moment("2020-05-13 12:45").format(eventFormat)
});

events[20].events[0] = sunday;
events[20].events[1] = monday;
events[20].events[3] = wensday;

events[21].events[0] = new Array({
    id: 4,
    title: 'תור שלישי',
    employeeId: 1,
    userId: "0502243024",
    start: moment("2020-05-17 08:45").format(eventFormat),
    end: moment("2020-05-17 10:45").format(eventFormat)
});
