import React, { useRef } from 'react';
import menuUserStyle from './menu-user.module.scss';
import { NavLink } from 'react-router-dom';
import { MdDashboard } from 'react-icons/md';
import { FaRegCalendarAlt } from 'react-icons/fa';
import { MdPeople } from 'react-icons/md';
import { BsGraphUp } from 'react-icons/bs';
import { MdSettings } from 'react-icons/md';


const MenuUser = () => {
    let showDropdown = 'none'

    const openDropdown = () => {
        if (showDropdown === 'none') {
            showDropdown = 'block';
        }
        else {
            showDropdown = 'none';
        }
    };

    return (
        <div className={menuUserStyle.Menu}>
            <p className={menuUserStyle.Logo}>
                Queue
            </p>
            <div className={menuUserStyle.User}>
                <p>שלום, דור לוי.</p>
            </div>
            <hr />
            <div className={menuUserStyle.Items}>
                <NavLink activeClassName={menuUserStyle.Current} exact to='/'>
                    <div className={menuUserStyle.Text}>
                        <MdDashboard />
                        <span>בית</span>
                    </div>
                </NavLink>
                <NavLink activeClassName={menuUserStyle.Current} to='/queue'>
                    <div className={menuUserStyle.Text}>
                        <FaRegCalendarAlt />
                        <span> יומן תורים</span>
                    </div>
                </NavLink>
                <NavLink activeClassName={menuUserStyle.Current} to='/customers'>
                    <div className={menuUserStyle.Text}>
                        <MdPeople />
                        <span onClick={openDropdown}>לקוחות</span>
                    </div>
                    <ul style={{display: showDropdown}} className={menuUserStyle.Dropdown}>
                        <li id="male">כל הלקוחות</li>
                        <li id="female">לקוחות מהשבוע</li>
                    </ul>
                </NavLink>
                <NavLink activeClassName={menuUserStyle.Current} to='/s'>
                    <div className={menuUserStyle.Text}>
                        <BsGraphUp />
                        <span>סטטיסטיקות</span>
                    </div>
                </NavLink>
                <NavLink activeClassName={menuUserStyle.Current} to='/settings'>
                    <div className={menuUserStyle.Text}>
                        <MdSettings />
                        <span>הגדרות</span>
                    </div>
                </NavLink>
            </div>
        </div>
    )
}

export default MenuUser;
