import React, { Component } from 'react'
import './calendar.css'
import {
    ScheduleComponent, Day, Week, Month, Inject, DragAndDrop, Resize, ScrollOptions, NavigateOptions,
    EventSettingsModel, ViewDirective, ViewsDirective, DragEventArgs, ResizeEventArgs
} from '@syncfusion/ej2-react-schedule';

class CalendarUser extends Component<any, any> {
    private data: EventSettingsModel = {
        dataSource: [
            {
                Id: 1,
                StartTime: new Date(2020, 4, 15, 10, 0), // year, month, day, hour, miniute
                EndTime: new Date(2020, 4, 15, 12, 30),
                Subject: "תור לרועי - תיספורת גברים",
                IsAllDay: false,
                Description: "שם הלקוח: דור לוי, מס' טלפון: 050-2243024",
                //RecurrenceRule: 'FREQ=DAILY;INTERVAL=1;COUNT=5', //Fills the calendar for 5 days
                IsReadonly: false, // Change to true to pravent delete and edit event
                IsBlock: false, // To block this time
            },
            {
                Id: 1,
                StartTime: new Date(2020, 4, 15, 13, 0), // year, month, day, hour, miniute
                EndTime: new Date(2020, 4, 15, 13, 30),
                Subject: "תור למתן - תיספורת גברים",
                IsAllDay: false,
                Description: "שם הלקוח: גלעד פוקס, מס' טלפון: 050-2243024",
                //RecurrenceRule: 'FREQ=DAILY;INTERVAL=1;COUNT=5', //Fills the calendar for 5 days
                IsReadonly: false, // Change to true to pravent delete and edit event
                IsBlock: false, // To block this time
            },
        ],
    };

    private onDragStart = (args: DragEventArgs) => {
        (args.scroll as ScrollOptions).scrollBy = 500; //scrolll when drag

        args.interval = 15;
    };

    private onResizeStart = (args: ResizeEventArgs) => {
        (args.scroll as ScrollOptions).scrollBy = 500;
        args.interval = 1;
    };

    render() {
        return (
            <div className="Calander">
                <ScheduleComponent

                    currentView="Week"
                    eventSettings={this.data}
                    timezone="Israel"
                    dragStart={() => this.onDragStart}
                    resizeStart={() => this.onResizeStart}
                >
                    <ViewsDirective>
                        <ViewDirective option="Day" />
                        <ViewDirective option="Week" />
                        <ViewDirective option="Month" />
                    </ViewsDirective>
                    <Inject services={[Day, Week, Month, DragAndDrop, Resize]} />
                </ScheduleComponent>
            </div>
        );
    }
}



export default CalendarUser;
