import React, { Component } from 'react';
import CalendarStyle from './calendar.module.scss';
import moment from 'moment';
import * as helper from './components/helper';
import { Events } from '../../../../models/system/events';
import { Event } from '../../../../models/system/event';

export default class CalendarUser extends Component {

    state = {
        startHour: 7,
        endHour: 20,
        currentWeekNumber: parseInt(moment(new Date()).format('WW'))
    }

    events: { [id: number]: Events } = helper.events;

    onSlotClick = (hour: string, date: string) => {
        console.log(hour, date);
        // Duration time of event - minutes
        // const durationOfEvent = moment.utc(moment(e, "YYYY-DD-MM HH:mm").diff(moment(s, "YYYY-DD-MM HH:mm"))).minutes();
        // const sMin = (moment(s).hour() * 60) + moment(s).minute();
    }


    render() {
        const allDaysWeek = helper.getWeekDaysByWeekNumber(this.state.currentWeekNumber);

        const slots: JSX.Element[] = []; // Hold the table
        for (let i = this.state.startHour; i < this.state.endHour; i++) {
            for (let j = 0; j < 4; j++) {
                const hour = moment(i + ":" + j * 15, "HH:mm").format("HH:mm");

                // Array of the dats week
                const days: any[] = [];
                // Array of boolean - if array[i] is true so there is a event in the slot
                const isEvents: Event[] = [];
                for (let i = 0; i < 7; i++) {
                    days.push(moment(new Date(allDaysWeek[i])).format("YYYY-DD-MM"));
                }

                const eventsWeek = this.events[this.state.currentWeekNumber].events; // all the events of week i
                eventsWeek.forEach((eventArr: Event[], i: number) => { // Every item in the array  is array of events 
                    eventArr.forEach((e: Event) => {
                        const date = moment(e.start).format("YYYY-DD-MM");
                        const startTime = moment(e.start).format("HH:mm");
                        const endTime = moment(e.end).format("HH:mm");
                        if (date === days[i]) {
                            if (moment(hour, 'HH:mm').isSameOrAfter(moment(startTime, 'HH:mm')) &&
                                (moment(hour, 'HH:mm').isBefore(moment(endTime, 'HH:mm')))) {
                                isEvents[i] = e;
                            }
                        }
                    });
                });

                slots.push((
                    <tr key={hour}>
                        <td className={CalendarStyle.Hours} style={{ cursor: 'initial' }}>{hour}</td>
                        {
                            allDaysWeek.map((day: any, i: number) => {
                                return (
                                    <td className={isEvents[i] ? CalendarStyle.Slot + " " + CalendarStyle.Event : CalendarStyle.Slot} onClick={() => this.onSlotClick(hour, day)}>
                                        {isEvents[i] ? isEvents[i].title : ""}
                                    </td>
                                )
                            })
                        }


                    </tr>
                ))
            }
        }


        // Return days and dats(The first row of table)
        const curMonth: string[] = [];
        const days = allDaysWeek.map((day: String, i: number) => {
            if (curMonth.indexOf(helper.monthNumberToHeb(moment(day.toString(), 'yyyy/MM/DD').month() + 1)) === -1) {
                curMonth.push(helper.monthNumberToHeb(moment(day.toString(), 'yyyy/MM/DD').month() + 1))
            }
            return (
                <th key={i * 11}>
                    {helper.hebDays[i]}
                    <br />
                    {moment(day.toString(), "yyyy/MM/DD").format('DD/MM')}
                </th>
            )
        })

        return (
            <div className={CalendarStyle.Calendar}>
                <div className={CalendarStyle.Header}>
                    <button onClick={() => this.setState({ currentWeekNumber: this.state.currentWeekNumber + 1 })}>next</button>
                    <button onClick={() => this.setState({ currentWeekNumber: this.state.currentWeekNumber - 1 })}>prev</button>
                    <button onClick={() => this.setState({ currentWeekNumber: parseInt(moment(new Date()).format('WW')) })}>current week</button>

                </div>
                <div className={CalendarStyle.Content}>
                    <table >
                        <tbody>
                            <tr>
                                <th className={CalendarStyle.TableHeader}>
                                    {curMonth.length === 1 ? curMonth : curMonth[0] + "-" + curMonth[1]}
                                </th>
                                {days}
                            </tr>
                            {slots}
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}
