import React, { useState } from 'react';
import chartStyle from './chart-home.module.scss';
import { Line } from 'react-chartjs-2';

const ChartHome = () => {

    const options = {
        responsive: true, maintainAspectRatio: false,
        scales: {
            xAxes: [{
                gridLines: {
                    color: "rgba(0, 0, 0, 0)",
                }
            }]
        }, 
        scaleLabel : "<%= Number(value).toFixed(0).replace('.', ',') + '°C'%>"

    };

    const data = (canvas: any) => {
        const ctx = canvas.getContext("2d")
        const gradient = ctx.createLinearGradient(100, 0, 100, 0);
        gradient.addColorStop(0, '#ffffff');
        gradient.addColorStop(.5, '#ded8fd');
        gradient.addColorStop(.4, '#bdb2fa');
        gradient.addColorStop(1, '#9a8cf5');
        gradient.addColorStop(1, '#7467ef');

        return {
            labels: ['ינואר', 'פבואר', 'מרץ', 'אפריל', 'מאי', 'יוני', 'יולי', 'אוגוסט', 'ספטמבר', 'אוקטובר', 'נובמבר', 'דצמבר'],
            datasets: [
                {
                    label: 'לקוחות',
                    data: [20, 50, 19, 100, 32, 42, 42, 46, 152, 141, 134, 150],

                    backgroundColor: gradient, // Put the gradient here as a fill color
                    borderColor: "#FFFAF0",
                    borderWidth: 2,
                    pointColor: "#fff",
                    pointStrokeColor: "#ff6c23",
                    pointHighlightFill: "#fff",
                    pointHighlightStroke: "#ff6c23",
                }
            ]
        }
    }

    return (
        <div className={chartStyle.Chart}>
            <Line
                options={options}
                data={data}
                height={300}
                width={300}

            />
        </div>
    )
}

export default ChartHome;
